import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
// import '../node_modules/font-awesome/css/font-awesome.min.css'; 
import Modal from 'react-bootstrap/Modal'
import Button from "react-bootstrap/Button";

import Image from "react-bootstrap/Image";
import './profile.scss';
const Profile = () => {
  const { isLoggedIn } = useSelector(state => state.auth);
  const { userData: currentUserData } = useSelector((state) => state.auth);
  const [user, setUser] = useState("");
  console.log(currentUserData)
  const dispatch = useDispatch();

 
    const values = [true, 'sm-down', 'md-down', 'lg-down', 'xl-down', 'xxl-down'];
    const [fullscreen, setFullscreen] = useState(true);
    const [show, setShow] = useState(false);
  
    function handleShow(breakpoint) {
      setFullscreen(breakpoint);
      setShow(true);
    }

  return (
    <section className="profileSection">
      <div className="profileImage">
        <Image src="https://via.placeholder.com/150" className="rounded float-left" alt="..." />
      </div>
      <div className="profileMeta">
        <h3>
          <strong>{currentUserData && currentUserData.name}</strong>
          <i className="fa fa-edit fa-edit"></i>
        </h3>
         <p>
          {currentUserData && currentUserData.designation}
        </p>
        <ul>
          <li>
            <strong>Email:</strong> {currentUserData && currentUserData.email}
          </li>
          <li>
            <strong>Cell Phone:</strong> {currentUserData && currentUserData.phone_number}
          </li>
          <li>
            <strong>Emergency Contact:</strong> {currentUserData && currentUserData.emergency_number}
          </li>
          <li>
            <strong>Skills:</strong> {currentUserData && currentUserData.skills}
          </li>
        </ul>
      </div>

      <>
      {values.map((v, idx) => (
        <Button key={idx} className="me-2" onClick={() => handleShow(v)}>
          Full screen
          {typeof v === 'string' && `below ${v.split('-')[0]}`}
        </Button>
      ))}
      <Modal show={show} fullscreen={fullscreen} onHide={() => setShow(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Modal</Modal.Title>
        </Modal.Header>
        <Modal.Body>Modal body content</Modal.Body>
      </Modal>
    </>
    </section>
  );
};

export default Profile;
