import { path_server} from "../../Constants";
import React,{useState, useEffect} from "react"
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import PropTypes from 'prop-types';
import { useNavigate } from 'react-router';

function Login({setToken}){
  let navigate = useNavigate();

    const axios = require('axios');
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const style = {
      headerBackground : {
        color: "white",
        backgroundImage:"url(http://intranet.genetech.pk/intranet/assets/images/login_bg.jpg)",
        backgroundRepeat:"no-repeat",
        backgroundSize:"cover",
        padding: "10px",
        fontFamily: "Arial",
        height:"100vh",
        display:"flex",
        justifyContent:"center",
        alignItems:" center",
        
      },
      formSidebar:{
        
      }
      
    };

    function validateForm() {
      //return email.length > 0 && password.length > 0;
      return true;
    }

    function handleSubmit(event) {
      event.preventDefault();
      const userData = {
        email: email,
        password:password
      }

      axios.post(path_server+"/api/login", userData)
      .then(res => {
        console.log(res.length);
        if (res.length === 0) {
          return Promise.reject(new Error("Empty list!"));
        }
        if(res){
          setToken(res.data.token);
          navigate('/user')
        }

      }).catch(error => { 
        
        console.log(error); 
        return Promise.reject(error); 
      });

    }

    return (
      <section style={style.headerBackground}>
        <div style={style.headerBackground}>

        </div>
        <div className="Login">
          <Form onSubmit={handleSubmit}>
            <Form.Group size="lg" controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                autoFocus
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group size="lg" controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button block size="lg" type="submit" disabled={!validateForm()}>
              Login
            </Button>
          </Form>
        </div>
      </section>
    );
}

export default Login;
Login.propTypes = {
  setToken: PropTypes.func.isRequired
};
