import {
    REGISTER_SUCCESS,
    REGISTER_FAIL,
    LOGIN_SUCCESS,
    LOGIN_FAIL,
    USER_DATA,
    LOGOUT,
  } from "../actions/type";
  import UserService from "../services/user.service";

      const cUser = UserService.getUserProfile().then(
      (response) => {
        // setUser(response.data.user);
       
        return response.data.user;
      },
      (error) => {
        const _content =
          (error.response && error.response.data) ||
          error.message ||
          error.toString();
          // setUser(_content);
          return _content;
      }
    );
  const state = {
    auth: JSON.parse(localStorage.getItem("user")),
    user: {}
  };
  
  const initialState = state
    ? { isLoggedIn: true, state }
    : { isLoggedIn: false, state: null };
  
  export default function (state = initialState, action) {
    const { type, payload } = action;
  
    switch (type) {
      case REGISTER_SUCCESS:
        return {
          ...state,
          isLoggedIn: false,
        };
      case REGISTER_FAIL:
        return {
          ...state,
          isLoggedIn: false,
        };
      case LOGIN_SUCCESS:
        return {
          ...state,
          isLoggedIn: true,
          
        };
      case LOGIN_FAIL:
        return {
          ...state,
          isLoggedIn: false,
          user: null,
        };
      case LOGOUT:
        return {
          ...state,
          isLoggedIn: false,
          user: null,
        };
      case USER_DATA:
        return {
          ...state,
          isLoggedIn: true,
          userData: payload.userData,
        };
      default:
        return state;
    }
  }
