  const [show, setShow] = useState(props.display);
  
  return (
      <Col className="alertBox">
      <ToastContainer className="p-3" position={position}>

        <Toast onClose={() => setShow(false)} bg={props.type} show={show} bg={props.type}>
          <Toast.Header>
            { 
