import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
// import '../node_modules/font-awesome/css/font-awesome.min.css'; 
import Modal from 'react-bootstrap/Modal'
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Image from "react-bootstrap/Image";
import './profile.scss';
import UserService from "../../../../../services/user.service";
import { UpdateUser } from "../../../../../actions/auth";

const Profile = () => {
  const { userData: user } = useSelector((state) => state.auth);
  const [show, setShow] = useState(false);
  let [phone, setPhone] = useState(user && user.phone_number);
  const [emergency, setEmergency] = useState(user && user.emergency_number);
  const [skills, setSkills] = useState(user && user.skills);
  const dispatch = useDispatch();
    function handleShow() {
      phone = user && user.phone_number;
      console.log(phone);
      setShow(true);
    }
    

    const data = {
      id : user && user.id,
      phone :phone,
      emergency :emergency,
      skills:skills,

    }
    async function handleSubmit(event) {
      event.preventDefault();
      UserService.setUserProfile(data).then(
        (response) => {
          console.log(response);
         // dispatch(UpdateUser(response.data));
        },
        (error) => {
          const _content =
            (error.response && error.response.data) ||
            error.message ||
            error.toString();
            return _content;
        }
      );

    }

  return (
    <section className="profileSection">
      <div className="profileImage">
        <Image src="https://via.placeholder.com/150" className="rounded float-left" alt="..." />
      </div>
      {user && 
      <div className="profileMeta">
        <h3>
          <strong>{user.name}</strong>
          <i className="fa fa-edit fa-edit" onClick={() => handleShow()}></i>
        </h3>
         <p>
          {user.designation}
        </p>
        <ul>
          <li>
            <strong>Email:</strong> {user.email}
          </li>
          <li>
            <strong>Cell Phone:</strong> {user.phone_number}
          </li>
          <li>
            <strong>Emergency Contact:</strong> {user.emergency_number}
          </li>
          <li>
            <strong>Skills:</strong> {user.skills}
          </li>
        </ul>
      </div>
      }

    {user && 
      <>
      <Modal show={show} onHide={() => setShow(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Profile</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Form onSubmit={handleSubmit}  className="editProfile">
            <Form.Group size="lg" controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                autoFocus
                disabled
                type="email"
                value={user.email}
                // onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group size="lg" controlId="phone">
              <Form.Label>Cell Phone</Form.Label>
              <Form.Control
                type="text"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
            </Form.Group>

            <Form.Group size="lg" controlId="emergency_phone">
              <Form.Label>Emergency Phone</Form.Label>
              <Form.Control
                type="text"
                value={emergency}
                onChange={(e) => setEmergency(e.target.value)}
              />
            </Form.Group>

            <Form.Group size="lg" controlId="phone">
              <Form.Label>Skills</Form.Label>
              <Form.Control
                type="text"
                value={skills}
                onChange={(e) => setSkills(e.target.value)}
              />
            </Form.Group>

            <Button block size="sm" type="submit" className="">
                Update
              </Button>

            
          
          </Form>
        </Modal.Body>
      </Modal>
    </>
  }
    </section>
  );
};

export default Profile;
